let lastScrollTop=0;const header=document.querySelector(".site-header"),scrollThreshold=100;let ticking=!1;function handleScroll(){const e=window.pageYOffset||document.documentElement.scrollTop;e<lastScrollTop||e<scrollThreshold?header.classList.remove("header-hidden"):e>lastScrollTop&&e>scrollThreshold&&header.classList.add("header-hidden"),lastScrollTop=e,ticking=!1}window.addEventListener("scroll",()=>{ticking||(window.requestAnimationFrame(()=>{handleScroll()}),ticking=!0)})